call apoc.load.json("https://api.openalex.org/works?mailto=cdaknhara3@gatech.edu&page=1&per-page=200&filter=is_paratext:false,is_retracted:false,authorships.institutions.id:https://openalex.org/I130701444") yield value


// create unique relation if a exists in b's related_works list
// displays nodes with relations
MATCH (a:Work), (b:Work) WHERE a.id IN b.related_works AND a.id <> b.id
MERGE (a)-[r: RELATED_TO]->(b)

WITH a, b

RETURN a,b 
